function bindModal(e,d,l){e=document.querySelector(e),d=document.querySelector(d),l=document.querySelector(l);const t=document.body;e.addEventListener("click",(e=>{e.preventDefault(),d.style.display="flex",t.classList.add("locked")})),l.addEventListener("click",(()=>{d.style.display="none",t.classList.remove("locked")})),d.addEventListener("click",(e=>{e.target===d&&(d.style.display="none",t.classList.remove("locked"))}))}bindModal(".modal__btn",".modal__wrapper",".modal__close");